    def zoomIn(self):
        print("zoom in")
        self.scaleImage(1.25)

    def zoomOut(self):
        print("zoom out")
        self.scaleImage(0.8)

    def setup_pushButton(self):
        import os
        current_exp = self.ivy_temps_path + str(self.spinBox.value())
        dirs = os.listdir( current_exp )

        # This would print all the files and directories
        for file in dirs:
            print(file)
            if 'ivy_stderr.txt' in file:
                with open(current_exp + '/' + file, 'r') as f:
                    content = f.read()
                    if content == '':
                        self.textBrowserIvySTDERR.setText('No output')
                    else:
                        self.textBrowserIvySTDERR.setText(content)
            elif 'ivy_stdout.txt' in file:
                with open(current_exp + '/' + file, 'r') as f:
                    content = f.read()
                    if content == '':
                        self.textBrowserIvySTDOUT.setText('No output')
                    else:
                        self.textBrowserIvySTDOUT.setText(content)
            elif '.err' in file:
                with open(current_exp + '/' + file, 'r') as f:
                    content = f.read()
                    if content == '':
                        self.textBrowserImplemSTDERR.setText('No output')
                    else:
                        self.textBrowserImplemSTDERR.setText(content)
            elif '.out' in file:
                with open(current_exp + '/' + file, 'r') as f:
                    content = f.read()
                    if content == '':
                        self.textBrowserImplemSTDOUT.setText('No output')
                    else:
                        self.textBrowserImplemSTDOUT.setText(content)
            elif '.iev' in file:
                plantuml_file = current_exp + "/plantuml.puml"
                generate_graph_input(current_exp + '/' + file, plantuml_file,show_frames=self.checkBox_frames.isChecked(),
                                                                             show_packet_details=self.checkBox_packet_content.isChecked())
                plantuml_obj = PlantUML(url="http://www.plantuml.com/plantuml/img/",  basic_auth={}, form_auth={}, http_opts={}, request_opts={})

                plantuml_file_png = plantuml_file.replace('.puml', '.png')
                plantuml_obj.processes_file(plantuml_file, plantuml_file_png)

                image = QImage(plantuml_file_png)
                if image.isNull():
                    QMessageBox.information(self, "Image Viewer", "Cannot load %s." % plantuml_file_png)
                    return

                self.imageLabel = QLabel()
                self.imageLabel.setBackgroundRole(QPalette.Base)
                self.imageLabel.setSizePolicy(QSizePolicy.Ignored, QSizePolicy.Ignored)
                self.imageLabel.setScaledContents(True)

                self.imageLabel.setPixmap(QPixmap.fromImage(image))
                self.scaleFactor = 1.0

                self.imageLabel.adjustSize()

                self.execution_trace_scrollArea.setWidget(self.imageLabel)
            elif '.pcap' in file:
                wiresharkTerm = embeddedTerminal(current_exp + '/' + file)
                #self.wireshark_area_scrollbar.setWidget(wiresharkTerm)
                            

    def scaleImage(self, factor):
        self.scaleFactor *= factor
        self.imageLabel.resize(self.scaleFactor * self.imageLabel.pixmap().size())

        self.adjustScrollBar(self.scrollArea.horizontalScrollBar(), factor)
        self.adjustScrollBar(self.scrollArea.verticalScrollBar(), factor)

        self.zoomInAct.setEnabled(self.scaleFactor < 3.0)
        self.zoomOutAct.setEnabled(self.scaleFactor > 0.333)

